import React, {Component} from 'react'
import axios from "axios";
import Task from "../Components/Task"
import { isConstructorDeclaration } from 'typescript'
import {Card, Header, Form, Input, Icon, Item, ModalActions} from "semantic-ui-react"

let endpoint = "http://localhost:9000"




class Home extends Component<any, any> {


  constructor(props: any){
    super(props)

    this.state = {
      task:"",
      items:[]
    }
  }

  getTask = () => {
    axios.get(endpoint + "/api/tasks").then((res)=>{
      if (res.data) {
        this.setState({
          items: res.data.map((task: any)=>{
            return (

              <Task 
              id={task._id}
              task_status={task.status}
              task={task.task}
              date={task.date}
              priority={task.priority}
              update={() => {this.updateTask(task._id)}}
              key={task._id}
              />
            );
          }),
        });
      }
      else {
        this.setState({
          items: []
        });
      }
    });
  };

  updateTask = (id : string) => {
    axios.put(endpoint + "/api/tasks/" + id).then((res) => {
      this.getTask()
    })

  }
  undoTask = (id : string) => {
    axios.put(endpoint + "/api/undotask/" + id).then((res) => {
      this.getTask()
    })
  }

  deleteTask = () => {

  }

  deleteAll = () => {
    axios.delete(endpoint + "/api/deleteAllTasks").then((res) => {this.getTask()})
  }

  onChange = (e: any) => {
    this.setState({
      [e.target.name] : e.target.value
    })
  }

  onSubmit = () => {
    let {task} = this.state;

    console.log(task)

    if (task) {
      axios.post(endpoint + "/api/task", 
      {task: task,})
      .then((res)=> {
        this.getTask();
        this.setState({
          task:"",
        });
        console.log(res)
      });
    }
  };


  componentDidMount() {
      this.getTask()
  }
  render() {
    return (
      <div>
        <div className='row'>
          <h2>
            To Do List
          </h2>
        </div>
        <div className='row'>
          <form>
            <input
            type="text"
            name="task"
            onChange={this.onChange}
            value={this.state.task}
            placeholder="Create A Task"
            />
          </form>

          <button onClick={this.onSubmit}>Submit</button>

          <button onClick={this.deleteAll}>Delete</button>
        </div>
        <div className='Task'>
          {this.state.items}
        </div>
      </div>
    )
  }
}

export default Home
